var $ = require('jquery');
var _ = require('lodash');
var DatavGui = require('@ali/datav-gui');

var share = window.share;
module.exports = function (container) {
  var viewport = {
    width : share.chartWidth,
    height: share.chartHeight,
    minWidth : share.chartMinWidth,
    minHeight: share.chartMinHeight
  }
  var chartDom = window.chartDom;
  var sizeGui = new DatavGui.GUI({
    autoPlace: false
  });
  var sizeChange = function () {
    chartDom.width(viewport.width).height(viewport.height);
    chart && chart.resize && chart.resize(viewport.width, viewport.height);
  }
  sizeGui.add(viewport, 'width', viewport.minWidth, window.innerWidth).name('宽度').onChange(sizeChange)
  sizeGui.add(viewport, 'height', viewport.minHeight, window.innerHeight).name('高度').onChange(sizeChange)

  container.html(sizeGui.domElement)
}
