var Store = require('./store');
var Action = require('./action');
var Reflux = require('../lib/reflux');
var React = require('react');
var _ = require('lodash');
var $ = require('jquery');


var BreadCrumb = React.createClass({
  mixins: [Reflux.connect(Store)],
  render: function () {
    return (<div className="com-info">{this.state.selectedComId ? <a href="javascript:;" className="back-to-parent" onClick={this.backToParent}><i className="datav-icon">&#xe643;</i>返回</a> : null}{this.state.selectedComId ? '  /  ' : null}{this.state.selectedComId || this.state.selectedComInfo.name} <small>v{this.state.selectedComInfo.version}</small></div>);
  },
  backToParent: function () {
    Action.selectParent(this.state);
  }
});


module.exports = BreadCrumb;
