var urllib = require('urllib');
function getUrl(url) {
  return new Promise(function (resolve) {
    urllib.request(url, {
      method: 'GET',
      timeout: 3000
    }, function (err, data) {
      if (err) {
        resolve({});
      } else {
        resolve(data);
      }
    })
  })
}

module.exports = {
  getUrl: getUrl
}

